weather_raw_df = pd.read_csv('NOAA_SF_weather.csv')
bike_df = pd.read_csv('2017-fordgobike-tripdata.csv')
for year in ['2018','2019']:
    for mth in ['01','02','03','04','05','06','07','08','09','10','11','12']:
        if year == '2019' and mth in ['05','06','07','08','09','10','11','12']:
            tmp_df = pd.read_csv(year+mth+'-baywheels-tripdata.csv')
        else:
            tmp_df = pd.read_csv(year+mth+'-fordgobike-tripdata.csv')
        bike_df = pd.concat([bike_df,tmp_df])
